import React, { Component } from "react";
import "./Home.css";
import axios from "axios";
import ApiData from "../../component/ApiData/ApiData";
import addUser from "../../asset/Images/icons8-add-user-male-50.png";
import icon from "../../asset/Images/icons8-thick-long-right-arrow-64.png";
import listIcon from "../../asset/Images/icons8-list-50.png";
import { withRouter } from "react-router-dom";
import logo from "../../asset/Images/icons8-virus-96.png";
import { connect } from "react-redux";

class Home extends Component {
  state = {
    load: false,
    api: false,
    data: [],
    firstName: "",
    lastName: "",
    gender: "",
    age: "",
    profession: "",
    query: "",
    pan: "No",
    aadhaar: "No"
    // user: this.props.location.state.user
  };
  componentDidMount() {
    let fetchData = [];
    axios.get("https://jsonplaceholder.typicode.com/photos").then(resp => {
      for (let i = 0; i < 6; i++) {
        fetchData.push({
          ...resp.data[i],
          key: resp.data[i].id
        });
      }
      this.setState({ data: fetchData });
    });
  }

  onHandleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }
  onHandleForm = () => {
    this.setState({ load: !this.state.load, api: false });
  };

  onHandleApi = () => {
    this.setState({ api: !this.state.api, load: false });
  };

  HandleSubmit = () => {
    return this.props.history.push({
      pathname: "/registration",
      state: { ...this.state }
    });
  };
  HandleReset = () => {
    this.setState({
      firstName: "",
      lastName: "",
      gender: "",
      age: "",
      profession: "",
      pan: "No",
      aadhaar: "No",
      query: ""
    });
  };
  render() {
    const list = this.state.data.map(k => {
      return <ApiData title={k.title} url={k.thumbnailUrl} key={k.id} />;
    });

    let homepage = null;
    if (!this.state.api && !this.state.load) {
      homepage = (
        <div className="para">
          <p>
            <strong>Coronaviruses (CoV) </strong> are a large family of viruses
            that cause illness ranging from the common cold to more severe
            diseases such as
            <a
              href="https://www.who.int/emergencies/mers-cov/en/"
              target="_blank"
              rel="noopener noreferrer"
            >
              Middle East Respiratory Syndrome (MERS-CoV)
            </a>
            and
            <a
              href="https://www.who.int/csr/sars/en/"
              target="_blank"
              rel="noopener noreferrer"
            >
              Severe Acute Respiratory Syndrome (SARS-CoV)
            </a>
            .
            <a
              href="https://www.who.int/emergencies/diseases/novel-coronavirus-2019"
              target="_blank"
              rel="noopener noreferrer"
            >
              A novel coronavirus (nCoV)
            </a>
            is a new strain that has not been previously identified in humans.
          </p>
          <p>
            <iframe
              title="corona-video2"
              width="560"
              height="475"
              src="https://www.youtube.com/embed/qF42gZVm1Bo"
              frameBorder="0"
              allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen=""
              className="frame"
            >
              &nbsp;
            </iframe>
          </p>
        </div>
      );
    }
    let apiList = null;
    if (this.state.api) {
      apiList = (
        <div>
          <div className="lists">
            <font style={{ marginRight: "1000px", fontSize: "20px" }}>
              <img src={listIcon} alt="Listicon" />
              CoronaVirus API Data
            </font>
          </div>
          {list}
        </div>
      );
    }
    let register = null;
    if (this.state.load) {
      register = (
        <div style={{ marginTop: "20px", marginBottom: "20px" }}>
          <h3 style={{ borderBottom: "0.25px solid", float: "left" }}>
            Online Registration <img src={addUser} alt="adduser" />
            <img src={icon} alt="icon" />
          </h3>

          <form className="form">
            <small id="emailHelp" className="form-text text-muted">
              We'll never share your details with anyone else.
            </small>
            <div className="form-group">
              <label htmlFor="exampleInputEmail1">First Name</label>
              <input
                type="text"
                name="firstName"
                onChange={event => this.onHandleChange(event)}
                className="form-control"
                id="exampleInputFirstName1"
                placeholder="Enter First Name"
              />
            </div>
            <div className="form-group">
              <label htmlFor="exampleInputPassword1">Last Name</label>
              <input
                type="text"
                name="lastName"
                onChange={event => this.onHandleChange(event)}
                className="form-control"
                id="exampleInputLastName1"
                placeholder="Enter First Name"
              />
            </div>
            <div className="form-group">
              <label htmlFor="exampleInputPassword1">Age</label>
              <input
                type="Number"
                name="age"
                onChange={event => this.onHandleChange(event)}
                className="form-control"
                id="exampleInputAge1"
                placeholder="Enter Age"
              />
            </div>
            <div className="form-check form-check-inline">
              <label htmlFor="exampleInputPassword1">
                Gender &nbsp;&nbsp;&nbsp;
              </label>
              <input
                className="form-check-input"
                onChange={event => this.onHandleChange(event)}
                type="radio"
                name="gender"
                id="exampleRadios1"
                value="Male"
              />
              <label className="form-check-label" htmlFor="exampleRadios1">
                Male
              </label>
              &nbsp;
              <input
                className="form-check-input"
                onChange={event => this.onHandleChange(event)}
                type="radio"
                name="gender"
                id="exampleRadios2"
                value="Female"
              />
              <label className="form-check-label" htmlFor="exampleRadios2">
                Female
              </label>
            </div>
            <hr />
            <div className="form-group">
              <label htmlFor="exampleFormControlSelect1">Occupation</label>
              <select
                className="form-control"
                id="exampleFormControlSelect1"
                onChange={event => this.onHandleChange(event)}
                name="profession"
              >
                <option value="">Select--</option>
                <option value="Service">Service</option>
                <option value="Business">Business</option>
                <option value="Gov. Employee">Gov. Employee</option>
                <option value="Self Employed">Self Employed</option>
                <option value="Others">Others</option>
              </select>
            </div>
            <div className="form-check">
              <input
                className="form-check-input"
                type="checkbox"
                name="pan"
                value="Yes"
                onClick={event => this.onHandleChange(event)}
                id="defaultCheck1"
              />
              <label className="form-check-label" htmlFor="defaultCheck1">
                Do you have Pan Card ?
              </label>
            </div>
            <div className="form-check">
              <input
                className="form-check-input"
                type="checkbox"
                name="aadhaar"
                value="Yes"
                onClick={event => this.onHandleChange(event)}
                id="defaultCheck2"
              />
              <label className="form-check-label" htmlFor="defaultCheck2">
                Do you have Aadhaar Card ?
              </label>
            </div>
            <div className="form-group">
              <label htmlFor="exampleFormControlTextarea1">Query ?</label>
              <textarea
                className="form-control"
                onChange={event => this.onHandleChange(event)}
                id="exampleFormControlTextarea1"
                rows="3"
                name="query"
              />
            </div>
            <button
              type="reset"
              className="btn btn-primary"
              onClick={this.HandleReset}
            >
              Reset
            </button>
            &nbsp;&nbsp;&nbsp;&nbsp;
            <button
              type="submit"
              className="btn btn-primary"
              onClick={this.HandleSubmit}
            >
              Submit
            </button>
          </form>
        </div>
      );
    }
    return (
      <div>
        <header className="header">
          <link
            rel="stylesheet"
            href="https://maxcdn.bootstrapcdn.com/bootstrap/4.4.1/css/bootstrap.min.css"
          />
          <script src="https://maxcdn.bootstrapcdn.com/bootstrap/4.4.1/js/bootstrap.min.js" />
          <button className="hbtn" onClick={this.onHandleForm}>
            Registration
          </button>
          <button className="hbtn" onClick={this.onHandleApi}>
            Data
          </button>
          <a href="/home" style={{ textDecoration: "none", color: "white" }}>
            <div style={{ marginLeft: "200px" }}>
              <img src={logo} alt="logo" />
              <font>CoronaVirus </font>{" "}
            </div>
          </a>
          <font
            style={{ marginLeft: "180px", marginTop: "30px", fontSize: "22px" }}
          >
            Welcome, {this.props.username}
          </font>
          <button
            className="bt"
            onClick={() => {
              this.props.history.replace("/");
            }}
          >
            Logout
          </button>
        </header>

        <main>
          {homepage}
          {register}
          {apiList}
        </main>
        <div className="list">Copyright 2020 - @CoronaVirus.com</div>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    username: state.user
  };
};
export default connect(mapStateToProps)(withRouter(Home));
